(function () {

    function Person (map, walkableTiles) {
        this.position = {x: 0, y: 0};
        this.map = map;
        this.walkableTiles = walkableTiles;
    };

    Person.prototype.moveVertical = function (step) {
        if ([0, this.map.length - 1].indexOf(this.position.y) !== - 1) {
            return;
        }
        var nextY = this.position.y + step;
        if (this.walkableTiles.indexOf(this.map[nextY][this.position.x]) === -1) {
            return;
        }
        this.position.y = nextY;
    };

    Person.prototype.moveHorizontal = function (step) {
        if ([0, this.map[0].length - 1].indexOf(this.position.x) !== - 1) {
            return;
        }
        var nextX = this.position.x + step;
        if (this.walkableTiles.indexOf(this.map[this.position.y][nextX]) === -1) {
            return;
        }
        this.position.x = nextX;
    };

    exports = module.exports = Person;

}).call(this);

